export default function Redcolor(sizearray,size,items,proid){
    const x=sizearray
   x.forEach(element=>{
       if(element.proid===proid)
    if(element.id===size){
        items.forEach(item=>{
    if(item.value===element.choose){
     if(this.state.choose)
    this.setState({choose:element.key})
}})
}})
}         